<?php

namespace Kodify\BlogBundle\Repository;

/**
 * CommentRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CommentRepository extends AbstractBaseRepository
{
    const LIST_DEFAULT_LIMIT = 10;

    public function byPostId($post_id, $limit = null, $offset = 0)
    {
        if (is_null($limit)) {
            $limit = static::LIST_DEFAULT_LIMIT;
        }

        return $this->findByPost($post_id, ['createdAt' => 'DESC'], $limit, $offset);
    }
}
